if ( ! function_exists('archivo_crear_directorio'))
{
	function archivo_crear_directorio($url)
	{
		$directorio =preg_split('/\//', $url);
		$url = '';
		foreach ($directorio as $key => $value) {
			$value = trim($value);
			if ($value == '')
		        continue;
			$url .= $value.'/';
			if(!file_exists($url)){
				mkdir($url);
			}
		}
		return $url;
	}
}
if ( ! function_exists('text_letter_UTF8decode')){
	function text_letter_UTF8decode($texto){
		$texto = trim($texto);
		$tmp = $texto;
		$longitud = 0;
		while (mb_detect_encoding($tmp)=="UTF-8")
		{
			$tmp = utf8_decode($tmp);
			$longitud++;
		}
		for ($i = 0; $i < $longitud-1 ; $i++)
		{
			$texto = utf8_decode($texto);
		}
		return $texto;
	}
}
