if ( ! function_exists('directory_map'))
{
	function directory_map($source_dir, $directory_depth = 0, $hidden = FALSE)
	{
		if ($fp = @opendir($source_dir))
		{
			$filedata	= array();
			$arbol	= array();
			$new_depth	= $directory_depth - 1;
			
			$source_dir	= rtrim($source_dir, DIRECTORY_SEPARATOR).DIRECTORY_SEPARATOR;

			while (FALSE !== ($file = readdir($fp)))
			{
				// Remove '.', '..', and hidden files [optional]
				if ( ! trim($file, '.') OR ($hidden == FALSE && $file[0] == '.'))
				{
					continue;
				}

				if (($directory_depth < 1 OR $new_depth > 0) && @is_dir($source_dir.$file))
				{
					$filedata[$file] = directory_map($source_dir.$file.DIRECTORY_SEPARATOR, $new_depth, $hidden);
					$path = $source_dir.$file;
					$nodo = new Nodo($file,false,$filedata[$file],$path);
					$arbol[] = $nodo;
				}
				else
				{
					$filedata[] = $file;
					$path = $source_dir.$file;
					$nodo = new Nodo($file,true,null,base_url().$path);
					$arbol[] = $nodo;
				}
			}

			closedir($fp);
			return $arbol;
		}
		return FALSE;
	}
}
if ( ! function_exists('getImageBase64'))
{
	function getImageBase64($id_organismo='bqa')
	{
		$header = ''; $watermarkup = ''; $footer = ''; $firm='';
		
		$blank_image = '';
		switch ($id_organismo) {
			case 'bqa'://
				$header = '';
				#marca de agua en blanco
				$watermarkup = $blank_image;
				$footer = '';
				$firm = $blank_image;
				$array_image = array('header' => $header, 'watermarkup'=>$watermarkup, 'footer'=>$footer, 'firm'=>$firm);
			break;
			default:
				$array_image = array('header' => $blank_image, 'watermarkup'=>$blank_image, 'footer'=>$blank_image, 'firm'=>$blank_image);
			break;
		}
		return $array_image;
	}
}
class Nodo{
	
	var $text;
	var $iconCls;
	var $leaf;
	var $path;
	var $children;

	function __construct($text='',$leaf=false, $children=array(),$path='') {
		$this->text = $this->removeExtension($text);
		$this->iconCls = $this->extension($text,$leaf);
		$this->leaf = $leaf;
		$this->path = $path;
		$this->children = $children;
	}
	function removeExtension($textFile){
		$pos = strrpos($textFile, ".");
		return $pos===false?$textFile:substr($textFile, 0,$pos);
	}
	function extension($filename,$leaf){
		$extensiones = array(
								'doc'  =>'icon-docx',
								'docx' =>'icon-docx',
								'pdf'  =>'icon-pdf',
								'ott'  =>'icon-docx',//libre office
								'sxw'  =>'icon-docx',//open office
								'xlsx' =>'icon-xlsx',
								'xls'  =>'icon-xlsx',
								'ppt'  =>'icon-pptx',
								'pptx' =>'icon-pptx',
								'bmp'  =>'icon-image',
								'gif'  =>'icon-image',
								'jpeg' =>'icon-image',
								'jpg'  =>'icon-image',
								'jpe'  =>'icon-image',
								'png'  =>'icon-image',
								'tiff' =>'icon-image',
								'tif'  =>'icon-image',
								'txt'  =>'icon-text',
								'text' =>'icon-text',
								'log'  =>'icon-log',
								'xml'  =>'icon-xml',
								'und'  =>'icon-und',
								'fld'  =>'folder',
								'html' =>'icon-html'
							);
		$ext = strrchr($filename, '.');
		if($ext===false){
			$ext = '.und';
		}
		$ext = !$leaf?'fld':substr($ext, 1);
		return array_key_exists($ext, $extensiones)?$extensiones[$ext]:'icon-und';
	}
}

if ( ! function_exists('generar_pdf')){
	function generar_pdf($html,$nombre_archivo='doc.pdf',$output='I'){
		$CI =& get_instance();
		$CI->load->library(array('tcpdf_lib'));
		// The '@' character is used to indicate that follows an image data stream and not an image file name
		#$this->load->library('tcpdf_lib');
		$pdf = new Tcpdf_lib(PDF_PAGE_ORIENTATION, PDF_UNIT, PDF_PAGE_FORMAT, true, 'UTF-8', false);

		// set document information
		$pdf->SetCreator(PDF_CREATOR);
		$pdf->SetAuthor('Tecnosystems S.A.S');
		$pdf->SetTitle('PDF');
		$pdf->SetSubject('PDF');
		$pdf->SetKeywords('Tecnosystems, Comparendo, PDF, Minuta, Formato');

		// set margins
		$pdf->SetMargins(PDF_MARGIN_LEFT, 45, PDF_MARGIN_RIGHT);

		// set auto page breaks
		$pdf->SetAutoPageBreak(TRUE, 35);

		// set image scale factor
		$pdf->setImageScale(PDF_IMAGE_SCALE_RATIO);
		// Set font
		// dejavusans is a UTF-8 Unicode font, if you only need to
		// print standard ASCII chars, you can use core fonts like
		// helvetica or times to reduce file size.
		$pdf->SetFont('dejavusans', '', 10, '', true);

		// Add a page
		// This method has several options, check the source code documentation for more information.
		$pdf->AddPage();
        
        // writeHTML($html, $ln=true, $fill=false, $reseth=false, $cell=false, $align='')
		// writeHTMLCell($w, $h, $x, $y, $html='', $border=0, $ln=0, $fill=0, $reseth=true, $align='', $autopadding=true)
		
		// Print text using writeHTMLCell()
		// $pdf->writeHTMLCell(0, 0, '', '', $html, 0, 1, 0, true, '', true);
		$pdf->writeHTML($html, true, false, true, false, '');

		// ---------------------------------------------------------
		@ob_end_clean();
		// Close and output PDF document
		// This method has several options, check the source code documentation for more information.
		$pdf->Output($_SERVER['DOCUMENT_ROOT'].DIRECTORY_SEPARATOR.$nombre_archivo, $output);
	}
}